CREATE MATERIALIZED VIEW tensorzero.ChatInferenceByIdView TO tensorzero.InferenceById\n(\n    `id_uint` UInt128,\n    `function_name` LowCardinality(String),\n    `variant_name` LowCardinality(String),\n    `episode_id` UUID,\n    `function_type` String\n)\nAS SELECT\n    toUInt128(id) AS id_uint,\n    function_name,\n    variant_name,\n    episode_id,\n    \'chat\' AS function_type\nFROM tensorzero.ChatInference;
