CREATE MATERIALIZED VIEW tensorzero.DynamicEvaluationRunEpisodeByRunIdView TO tensorzero.DynamicEvaluationRunEpisodeByRunId\n(\n    `episode_id_uint` UInt128,\n    `variant_pins` Map(String, String),\n    `datapoint_name` Nullable(String),\n    `tags` Map(String, String),\n    `is_deleted` Bool,\n    `updated_at` DateTime64(6, \'UTC\'),\n    `run_id_uint` UInt128\n)\nAS SELECT\n    * EXCEPT run_id,\n    toUInt128(run_id) AS run_id_uint\nFROM tensorzero.DynamicEvaluationRunEpisode\nORDER BY\n    run_id_uint ASC,\n    episode_id_uint ASC;
