CREATE MATERIALIZED VIEW tensorzero.TagChatInferenceView TO tensorzero.TagInference\n(\n    `function_name` LowCardinality(String),\n    `variant_name` LowCardinality(String),\n    `episode_id` UUID,\n    `inference_id` UUID,\n    `function_type` String,\n    `key` String,\n    `value` String\n)\nAS SELECT\n    function_name,\n    variant_name,\n    episode_id,\n    id AS inference_id,\n    \'chat\' AS function_type,\n    key,\n    tags[key] AS value\nFROM tensorzero.ChatInference\nARRAY JOIN mapKeys(tags) AS key;
